import React from "react";

const User = ({
  handler,
  display_name,
  location,
  description,
  url,
  created_at,
  followers_count,
  favourites_count,
  statuses_count,
  verified,
  profile_image_url,
}) => {
  return (
    <div className="card" id="author_card">
      <img
        src={profile_image_url}
        className="card-img-top"
        alt={display_name}
      />
      <div class="card-body">
        <h5 className="card-title">
          {" "}
          {display_name} {verified && <i class="fas fa-check-circle"></i>}
          <a href={"/show/" + handler}>@{handler}</a>
        </h5>
        <p>
          <i class="fas fa-map-marker-alt"></i> {location}
          <br />
          <i class="fas fa-calendar-alt"></i> Joined since {created_at}
        </p>
        <p>
          <span class="text-primary">{statuses_count}</span> Tweets <br />
          <span class="text-primary">{followers_count}</span> Followers <br />
          <span class="text-primary">{favourites_count}</span> Likes <br />
        </p>

        <p class="card-text">{description}</p>
        <a href={url} class="btn btn-primary">
          More
        </a>
      </div>
    </div>
  );
};

export default User;
