import React, { Component } from "react";
import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";
import { searchUser } from "../../services/userService";
import { ThemeProvider, withStyles } from "@material-ui/core/styles";
import blue from "@material-ui/core/colors/blue";

const CssTextField = withStyles({
  root: {
    "& label.Mui-focused": {
      color: "white",
    },
    "& .MuiInput-underline:after": {
      borderBottomColor: "white",
    },
    "& .MuiOutlinedInput-root": {
      "& fieldset": {
        borderColor: "white",
      },
      "&:hover fieldset": {
        borderColor: "white",
      },
      "&.Mui-focused fieldset": {
        borderColor: "white",
      },
    },
  },
})(TextField);

class SearchBox extends Component {
  state = { searchedUsersResult: [], inputSearchedValue: "" };

  handleOnChange = async ({ currentTarget: search }) => {
    //console.log(search);
    const searchedUsersResult = await searchUser(search.value);
    this.setState({ searchedUsersResult });
  };

  handleEnter = ({ key, target }) => {
    if (key === "Enter") window.location = "/tweets/" + target.value;
  };

  render() {
    return (
      <Autocomplete
        id="free-solo-demo"
        freeSolo
        options={this.state.searchedUsersResult}
        renderInput={(params) => (
          <CssTextField
            {...params}
            style={{ width: "300px", margin: 10 }}
            size="small"
            label="Twitter Handler"
            margin="normal"
            variant="outlined"
            color="secondary"
            type="search"
            name="searchedHandler"
            onChange={this.handleOnChange}
            // onKeyPress={this.handleEnter}
          />
        )}
      />
    );
  }
}

export default SearchBox;
