import React, { Component } from "react";
import Tweet from "./Tweet";
import getTweets from "../services/tweetService";

class Tweets extends Component {
  state = {
    tweets: [],
  };

  componentDidMount() {
    const { data } = getTweets("nodejs");
    console.log(data);
    this.setState({ data });
  }

  render() {
    return (
      <React.Fragment>
        this.tweets.map((t) => {<Tweet tweetContent={t} />})
      </React.Fragment>
    );
  }
}

export default Tweets;
