import React from "react";

const User = ({ user }) => {
  console.log(user);
  const {
    handler,
    display_name,
    location,
    description,
    url,
    created_at,
    followers_count,
    favourites_count,
    statuses_count,
    verified,
    profile_image_url,
  } = user;
  console.log(url);
  return (
    <div className="card mb-3" id="author_card">
      <div class="row no-gutters">
        <div class="col-md-4">
          <img
            src={profile_image_url}
            className="card-img"
            alt={display_name}
          />
        </div>
        <div class="col-md-8">
          <div className="card-body">
            <h5 className="card-title">
              {display_name}{" "}
              {verified && <i className="fa fa-check-circle"></i>}
              <a href={"/show/" + handler}>@{handler}</a>
            </h5>
            <p>
              <i className="fas fa-map-marker-alt"></i> {location}
              <br />
              <i className="fas fa-calendar-alt"></i> Joined since {created_at}
            </p>
            <p>
              <span className="text-primary">{statuses_count}</span> Tweets{" "}
              <br />
              <span className="text-primary">
                {followers_count}
              </span> Followers <br />
              <span className="text-primary">
                {favourites_count}
              </span> Likes <br />
            </p>

            <p className="card-text">{description}</p>
            <a className="btn btn-primary" href={url} role="button">
              More
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default User;

